  <li *ngFor="let car of cars">
    <a [routerLink]="['/car', car.link.url]">
      <strong>
        {{ car.link.caption }}
      </strong>
      <span>
        {{ car.cost | currency:'EUR' }}
      </span>
    </a>
  </li>
